import { useState, useEffect, useRef } from 'react'
import { ethers } from "ethers"
import { Row, Col, Card, Button } from 'react-bootstrap'
import { Link } from "react-router-dom"
const toWei = (num) => ethers.utils.parseEther(num.toString())

export default function MyAccount({ contract, account }) {
  const [loading, setLoading] = useState(true)
  const [membership, setMembership] = useState(null)
  const loadMyAccount = async () => {
    const result = await contract.checkMembers()
    const membership = result
    setMembership(membership)
    setLoading(false)
  }
  const joinMembership = async () => {
    await (await contract.membership({value: toWei(1)})).wait()
    loadMyAccount()
  }

  useEffect(() => {
    !membership && loadMyAccount()
  })

  if (loading) return (
    <main style={{ padding: "1rem 0" }}>
      <h2>Loading...</h2>
    </main>
  )
  return (
    <div className="flex justify-center">
      <div className="flex justify-center">
        {membership == false?
          <div className="px-5 py-3 container">
            <>
            <main>
            {[
                    'Secondary'
                ].map((variant) => (
                    <><Card
                    bg={'light'}
                    key={variant}
                    text={'dark'}
                    style={{ width: '40rem' }}
                    className="mb-2 "
                    padding= "1rem 13rem"
                  >
                    <Card.Header bg='warning'>Membership</Card.Header>
                    <Card.Body>
                      <Card.Title><h1>Do you wanna join us?</h1></Card.Title>
                      <Card.Text>
                        Come and recieve our 10% discount ONLY FOR our members!
                      </Card.Text>
                      <h2>Why join us</h2>
                        <ul>
                          <li>Buy music with a 10% discount</li>
                          <li>Resale your token anytime at any price</li>
                          <li>And it only costs you 1 ETH!</li>
                        </ul>
                      <Button onClick={() => joinMembership()} variant="primary">
                        Join for 1 ETH
                      </Button>
                    </Card.Body>
                  </Card>
                  </>
                ))}
            </main>
            </>
          </div>
          : (
            <main style={{ padding: "1rem 17rem" }}>
              <Card
                    text={'dark'}
                    style={{ width: '40rem'}}
                    className="mb-2 "
                    >
                    <Card.Header>Membership</Card.Header>
                    <Card.Body>
                        <Card.Title>You're already our member! </Card.Title>
                        <Card.Text>
                        Enjoy your 10% discount and have some fun!
                        </Card.Text>
                        <Button as={Link} to="/" variant="primary">Enjoy your music</Button>
                    </Card.Body>
                    </Card>
              
            </main>
          )}
      </div>
    </div>
  );
}
